PREFIX brick: <https://brickschema.org/schema/1.1/Brick#>
PREFIX bf: <https://brickschema.org/schema/1.1/BrickFrame#>

SELECT DISTINCT ?sensor ?setpoint WHERE {
  # Find all Zone Air Temperature Sensors
  ?sensor a brick:Zone_Air_Temperature_Sensor .

  # Find Setpoints that are related to the same zone (or directly linked via a control relationship)
  ?setpoint a brick:Zone_Air_Temperature_Setpoint .

  # Connectedness: either they share a common zone, or a sensor is linked to a setpoint through a bf:hasPoint / bf:isPointOf relationship
  {
    # Shared zone relationship
    ?zone a brick:HVAC_Zone .
    ?sensor bf:isPointOf ?zone .
    ?setpoint bf:isPointOf ?zone .
  } UNION {
    # Direct control relationship (sensor provides feedback to the setpoint)
    ?sensor bf:feeds ?setpoint .
  } UNION {
    # Reverse direction of a control relationship
    ?setpoint bf:feeds ?sensor .
  }
}
ORDER BY ?sensor ?setpoint
